CREATE TABLE "public"."Enrollment"("Id" serial NOT NULL, "CourseId" integer NOT NULL, "ParticipantId" integer NOT NULL, "Status" integer NOT NULL, "MotivationLetter" text NOT NULL, "MotivationLetterRank" integer NOT NULL, "InvitationDeadline" date NOT NULL, "WantsEcts" boolean NOT NULL, "OnlineCourse" text NOT NULL, "Project" text NOT NULL, "CertificateConfirmed" boolean NOT NULL, PRIMARY KEY ("Id") , FOREIGN KEY ("CourseId") REFERENCES "public"."Course"("Id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("ParticipantId") REFERENCES "public"."Participant"("Id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("Id"));
