CREATE TABLE "public"."SchoolClassRequest" ("id" serial NOT NULL, "classId" integer NOT NULL, "offerId" integer NOT NULL, "possibleDays" integer[] NOT NULL, "assigned_day" integer, PRIMARY KEY ("id") , FOREIGN KEY ("classId") REFERENCES "public"."SchoolClass"("id") ON UPDATE cascade ON DELETE cascade, FOREIGN KEY ("offerId") REFERENCES "public"."ScientistOffer"("id") ON UPDATE cascade ON DELETE cascade);COMMENT ON TABLE "public"."SchoolClassRequest" IS E'A request from a school class for a specific offer from a scientist. It can be fulfilled by given an assignment.';
